def count_xmas_in_grid(grid):
    word = "XMAS"
    word_length = len(word)
    count = 0
    rows = len(grid)
    cols = len(grid[0]) if rows > 0 else 0

    # Check horizontally and backwards
    for r in range(rows):
        for c in range(cols):
            # Horizontal right
            if c + word_length <= cols and grid[r][c:c + word_length] == word:
                count += 1
            # Horizontal left
            if c - word_length + 1 >= 0 and grid[r][c - word_length + 1:c + 1][::-1] == word:
                count += 1

    # Check vertically
    for c in range(cols):
        for r in range(rows):
            # Vertical down
            if r + word_length <= rows and ''.join(grid[r + i][c] for i in range(word_length)) == word:
                count += 1
            # Vertical up
            if r - word_length + 1 >= 0 and ''.join(grid[r - i][c] for i in range(word_length)) == word:
                count += 1

    # Check diagonals
    for r in range(rows):
        for c in range(cols):
            # Diagonal down-right
            if r + word_length <= rows and c + word_length <= cols:
                if ''.join(grid[r + i][c + i] for i in range(word_length)) == word:
                    count += 1
            # Diagonal up-right
            if r - word_length + 1 >= 0 and c + word_length <= cols:
                if ''.join(grid[r - i][c + i] for i in range(word_length)) == word:
                    count += 1
            # Diagonal down-left
            if r + word_length <= rows and c - word_length + 1 >= 0:
                if ''.join(grid[r + i][c - i] for i in range(word_length)) == word:
                    count += 1
            # Diagonal up-left
            if r - word_length + 1 >= 0 and c - word_length + 1 >= 0:
                if ''.join(grid[r - i][c - i] for i in range(word_length)) == word:
                    count += 1

    return count

